function horarioReservacion(){var e=document.getElementById("fechaReservacion").value,o=document.getElementById("estilista").value,n=document.getElementById("domicilio").value,r=document.getElementById("servicio").value;""!=e&&""!=o&&""!=n&&$.ajax({url:"../controller/reservacionController.php",type:"POST",data:{estilista:o,fechaReservacion:e,domicilio:n,idServicio:r,funcion:"mostrarHorariosDisponibles"}}).done(function(e){console.log(e);var o=JSON.parse(e),n=document.getElementById("horaReservacion");for(n.innerHTML="",$("#horaReservacion").removeAttr("disabled"),i=0;i<o.length;i++)agregarHorario(o[i]);if(0==o.length){var r=document.getElementById("horaReservacion"),a=document.createElement("option");a.value="",a.innerHTML="No hay horarios Disponibles",a.disabled=!0,a.selected=!0,r.appendChild(a)}})}function agregarHorario(e){var o=document.getElementById("horaReservacion"),n=document.createElement("option");n.value=e,n.innerHTML=e,o.appendChild(n)}