function limpiarFiltroReservacion(){var e=document.getElementById("formLimpiar"),t=e.elements;for(e.reset(),i=0;i<t.length;i++)switch(field_type=t[i].type.toLowerCase(),field_type){case"date":t[i].value="";break;case"select-one":t[i].selectedIndex=-1;break;case"text":case"number":t[i].value=""}e.submit()}